import React, { useCallback } from 'react';
import './HomeScreen.css';
import { loadFull } from "tsparticles";
import Particles from "react-tsparticles";

function HomeScreen() {
    const particlesInit = useCallback(async (engine) => {
        // This loads the full tsParticles package
        await loadFull(engine);
    }, []);

    const particlesOptions = {
        background: {
            color: {
                value: "#000", // Set background color
            },
        },
        particles: {
            number: {
                value: 50, // Number of particles
            },
            size: {
                value: 3, // Size of particles
            },
            move: {
                enable: true,
                speed: 2, // Particle movement speed
            },
            lineLinked: {
                enable: true,
                color: "#ffffff", // Lines color
            },
        },
    };

    return (
        <div className="home-screen">
            {/* Particles Background */}
            <Particles
                id="tsparticles"
                init={particlesInit}
                options={particlesOptions}
                className="particles"
            />

            {/* Header */}
            <header className="header">
                <div className="logo">
                    <img src="/path-to-logo.png" alt="logo" />
                </div>
                <nav>
                    <ul>
                        <li><a href="#about">About Me</a></li>
                        <li><a href="#portfolio">View Portfolio</a></li>
                        <li><a href="#contact">Contact</a></li>
                    </ul>
                </nav>
            </header>

            {/* Main Content */}
            <div className="main-content">
                <h1>HELLO, I'M <span className="name">NOOR</span>.</h1>
                <h2>I'M A COMPUTATIONAL MEDIA MAJOR.</h2>
                <p>Also, I like making websites.</p>
            </div>

            {/* Buttons */}
            <div className="buttons">
                <a href="#about" className="button about">About Me</a>
                <a href="#portfolio" className="button view-portfolio">View Portfolio</a>
                <a href="#contact" className="button contact">Contact</a>
            </div>
        </div>
    );
}

export default HomeScreen;
